import React, { setState } from 'react';
import { Link } from 'react-router-dom';
import { itemInfo } from 'Helper';
import CartItem from './components/CartItem';

function Cart(props) {
    let [cartItems, setCartItems] = setState(props.location.state);

    const changeCartCount = (e) => {
        const currentCount = e.target.value;
        const item = e.target.parentNode.classList.item(0);
        let newCart = {...cartItems};
        newCart[item] = currentCount;
        setCartItems(newCart);
    }

    return (
        <div>
            <h2>Your cart</h2>
            <Link to={{
                pathname: '/shop',
                state: cartItems}}>Back to shop</Link>
                {itemInfo.map((item, index) => 
                    <CartItem 
                        itemPic={item.pic}
                        itemName={item.name}
                        updateCount={changeCartCount}
                        itemCount={cartItems[item.pic]}
                        key={index}></CartItem>)}
            <h3>Total cost: {cartItems.keys().reduce(
                (prev, cur, index) => 
                prev + cartItems[cur] * itemInfo[index].price, 0)}</h3>
            <button className='checkout-button'>Checkout?</button>
        </div>
    )
}

export default Cart;
